import React, { useState } from "react";

//import styles from "./Pages.module.css"


export const Pages = ({allDogs})=>{

const maximunIndex = Math.ceil(allDogs.length/8)

const [pages, setPages] = useState(1);

let buttons = [];

for(i = 1; i <= maximunIndex; i++) {
    buttons.push(i);
}
const handleClickNext = (e)=> {
if(e && pages < maximunIndex ) {
    setPages(pages + 1)
};
};

const handleClickPrev = (e)=>{
if(e && pages > 1) {
    setPages(pages - 1)
}; 
};
    return(
        <div className={styles.containerButtons} >
            <button className={styles.prev} onClick={handleClickPrev}>{'<'}</button>

            <button className={styles.next} onClick={handleClickNext}>{'>'}</button>
        </div>
    )
}
