import React, { useEffect } from "react";
import { useDispatch, useSelector} from "react-redux";
import { getAllDogs } from "../../redux/action-creators";
import { Card } from "../Card/Card";
import { Link } from "react-router-dom";

export const Home = () => {
    const dispatch = useDispatch();
    const allDogs = useSelector((state) => state.allDogs);

    useEffect(() => {
        dispatch(getAllDogs())
    }, [dispatch])


   return (
        <div>
            Home
            <Link to = "/create">CREAR PERRITO</Link>
            <Grid allDogs= {allDogs}/>
            {allDogs.map((dog) => {
                if(dog.hasOwnProperty("temperament")) {
                return (
                <Card
                key={dog.id} 
                name={dog.name}
                weight={dog.weight.metric}
                image={dog.image.url}
                temperament={dog.temperament}
                id={dog.id}
                
            />
                )
                } else if (dog.hasOwnProperty("tempers")) {
                    return (
                    <Card
                key={dog.id} 
                name={dog.name}
                weight={dog.weight.metric}
                image={dog.image.url}
                temperament={dog.tempers.map((temper) => temper.temper_name)}
                id={dog.id}
            />
                    )
                }

                else {
                    return (
                        <Card
                        key={dog.id} 
                        name={dog.name}
                        weight={dog.weight.metric}
                        image={dog.image.url}
                        id={dog.id}                       
                    />
                        )
                }
            })}

        </div>
    )
}
