import axios from "axios";
import { GET_ALL_DOGS } from "../action-types";
import { FILTER_DOGS_BY_TEMPER } from "../action-types";
const { REACT_APP_GET_ALL_DOGS } = process.env;

export const getAllDogs = () => {
    return async (dispatch) => {
        const response = await axios.get(REACT_APP_GET_ALL_DOGS);
        let dogs = response.data.map((dog) => dog.tempers ? dog.temperament = (dog.tempers.map((temper) => temper.temper_name).toString()) : null)
        dispatch ({ type: GET_ALL_DOGS, payload: dogs });
    };
};

export const filterDogsByTemper = (payload) => {
    
    return  (
        {type: FILTER_DOGS_BY_TEMPER, payload}
    )
}
