import { GET_ALL_DOGS, FILTER_DOGS_BY_TEMPER } from "../action-types";

const initialState = {
    dogs: [],
    allDogs: [],
}

export default function rootReducer (state = initialState, action) {
    switch (action.type) {
        case GET_ALL_DOGS: 
            return {
                ...state,
                dogs: action.payload,
                allDogs: action.payload,
            }
            case FILTER_DOGS_BY_TEMPER:
                let filteredDogs = []; 
                if(action.payload === "all") {
                    filteredDogs = state.dogs;
                } else {
                    filteredDogs = state.dogs.filter((dog) => (
                        (dog.temperament && dog.temperament.includes(action.payload)) ||
                        (dog.tempers && dog.tempers.map((temper) => temper.temper_name.includes(action.payload)))
                        
                    ))
                }
            return {
                ...state,
                allDogs: filteredDogs
            }
        default:
            return state;
    }
}
